jQuery.sap.declare("com.ril.IntMovMatPos.util.formatter");
com.ril.IntMovMatPos.util.Formatter = {
 date : function(value) {
		if (value) {
			var oDateFormat = sap.ui.core.format.DateFormat
					.getDateTimeInstance({
						pattern : "dd-MM-yyyy"
					});
			//var oDateFormat = sap.ui.core.format.DateFormat.getDateTimeInstance({pattern : "dd-MMM-yyyy at HH:MM:SS"});
			return oDateFormat.format(new Date(value));
		} else {
			return value;
		}

	},
Currency : function(quanAmt, uomCur) {
			var val;
			var curr;
			if (uomCur == "INR") {
				curr = "en-IN";
			} else {
				curr = "en-US";
			}
			if (quanAmt && uomCur) {
				var oNumberFormat = sap.ui.core.format.NumberFormat.getFloatInstance({
					maxFractionDigits: 3,
					groupingEnabled: true,
					groupingSeparator: ",",
					decimalSeparator: "."
				}, new sap.ui.core.Locale(curr));

				val = oNumberFormat.format(quanAmt) + " (" + uomCur + ")";
				return val;

				//    return quanAmt + " ("+uomCur+")" ;
			} else if (quanAmt) {
				var oNumberFormat = sap.ui.core.format.NumberFormat.getFloatInstance({
					maxFractionDigits: 3,
					groupingEnabled: true,
					groupingSeparator: ",",
					decimalSeparator: "."
				}, new sap.ui.core.Locale("en-IN"));

				val = oNumberFormat.format(quanAmt);
				return val;
				//return quanAmt;
			} else if (uomCur) {
				return uomCur;
			} else {
				return "";
			}
     	},
     	Status: function(value){
			if(value =="E"){
				return "Error";
			} else if(value =="I"){
				return "Information";
			} else if(value=="S"){
				return "Success";
			}
		},
		
		//GST changes start segment
		storageType : function(value1, value2) {
		try {
			if (value1 && value2)
				return value1 + " (" + value2 + ")";
			else if (value1)
				return value1;
			else if (value2)
				return "(" + value2 + ")";
			else
				return "";
		} catch (oError) {
			return "";
		}
	},
	
		//GST changes end segment
	ButtonVisible:function(val){
		if(val == "A4"){
			return false;
		}
		else 
			return true;
		
	},
	
	codeOnServer : function(){
	    var regExp = /^(http:\/\/|https:\/\/)(localhost|[0-9]{1,3}.[0-9]{1,3}.[0-9]{1,3}.[0-9]{1,3}):/;//Regular Expression to check address bar
	    var url = window.location.href;
	    return (!regExp.test(url));
	},
	getServiceUrl : function(){
	    var serverUrl = "/sap/opu/odata/sap/Z_FIORI_LOG_SW_MATERIAL_DG_SRV";
	    var localUrl = "../IntMovMatPos/proxy/sap/opu/odata/sap/Z_FIORI_LOG_SW_MATERIAL_DG_SRV";
	    if(this.codeOnServer()){
	        return serverUrl;
	    }
	    else{
	        return localUrl;
	    }
	},
	getServiceUrlMaterial:function(){
		var serverUrl = "/sap/opu/odata/sap/Z_FIORI_LOG_SW_MATERIAL_DG_SRV";
	    var localUrl = "../IntMovMatPos/proxy/sap/opu/odata/sap/Z_FIORI_LOG_SW_MATERIAL_DG_SRV";
	    if(this.codeOnServer()){
	        return serverUrl;
	    }
	    else{
	        return localUrl;
	    }
	},
	getServiceUrlWarehouse:function(){
		var serverUrl = "/sap/opu/odata/sap/Z_FIORI_LOG_SW_MATERIAL_DG_SRV";
	    var localUrl = "../IntMovMatPos/proxy/sap/opu/odata/sap/Z_FIORI_LOG_SW_MATERIAL_DG_SRV";
	    if(this.codeOnServer()){
	        return serverUrl;
	    }
	    else{
	        return localUrl;
	    }
	},
	
 
};